import React from "react";
import Categories from "../components/Categories";
import Sort from "../components/Sort";
import PizzaBlock from "../components/PizzaBlock";
import pizzas from "../assets/pizzas.json";
 function Home() {
    const [items,setItems] = React.useState([]);
    const [categoryId,setCategoryId] = React.useState(0);
    const [sortType,setSortType] = React.useState(0);
    const allPizzas=pizzas
    const meatPizzas=pizzas.filter((pizza)=>pizza.category[1]===1)
    const vegPizzas=pizzas.filter((pizza)=>pizza.category[1]===2)
    const grillPizzas=pizzas.filter((pizza)=>pizza.category[1]===3)
    const spicyPizzas=pizzas.filter((pizza)=>pizza.category[1]===4)
    const closedPizzas=pizzas.filter((pizza)=>pizza.category[1]===5)
    window.scrollTo(0, 0);
  return (
    <> <div className="container">
      <div className="content__top">
        <Categories value={categoryId} onClickCategory={(id)=>setCategoryId(id)}/>
        <Sort />
      </div>
      <h2 className="content__title">Все пиццы</h2>
      <div className="content__items">
        {
        
        categoryId===0? allPizzas.
        map((obj) => (
          <PizzaBlock {...obj} key={obj.id}/>)):alert(1)
        }
      </div>
      </div>
    </>
  );
}
export default Home;
